package all;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// bitwise
		
		
		/* bitwise operator perform specified operation
		 * 
		 * true or false values
		 * 
		 *  & bitwise AND
		 * | bitwise OR
		 * 
		 * */
		
		
		int a=8;
		int b=5;
		
		System.out.println(a>b & a<b);// both conditions true
		System.out.println(a<b | a>b); // one condition true
		
		int c=4;
		int d=2;
		System.out.println(c<d|d<c); 
		
		
		
		
		
		
		
		
		
		
	}

}
