package all;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* arithmetic operators are used to perform common mathematical operations
		 * 
		 * + addition 
		 * - subtraction 
		 * * multiplication 
		 * / division 
		 * % modulus 
		 * 
		 */

		// Addition 
		
		int a=323567890;
		int b=437462638;
		int c=a+b;
		System.out.println(c);
		
		// or
		
		int a1=3,b1=4,c1=a1+b1;
		System.out.println(c1);
		
		//or
		
		int a2=3, b2=4, c2=0;
		System.out.println(c2=a2+b2);
		
		// Subtraction
		
		int d=5;
		int e=8;
		int f=d+e;
		System.out.println(f);
		
		//or
		
		int d1=5, e1=8,f1=d1+e1;
		System.out.println(f1);
		
		//or
		
		int d2=5, e2=8, f2;
		System.out.println(f2=d2+e2);
		
		
	   // Multiplication
		
		int g=9;
		int h=2;
		int i=g*h;
		System.out.println(i);
		
		//or
		
		int g1=9, h1=2, i1=g1*h1;
		System.out.println(i1);
		
		//or
		
		int g2=9,h2=2,i2=0;
		System.out.println(i2=g2*h2);
		
		
		//Division
		
		int j=6;
		int k=7;
		int l=j/k;
		System.out.println(l);
		
		
		//or
		
		int j1=6, k1=7, l1=j1/k1;
		System.out.println(l1);
		
		
		//or
		
		int j2=6, k2=7, l2;
		l2=j2/k2;
		System.out.println(l2=j2/k2);
		
		
		// Modulus or Remainder
		
		
		int m=8;
		int n=4;
		int o=m%n;
		int m3=8, n3=4, o3=m3%n3;
		System.out.println(o+" "+o3);
		
		//or
		
		int m1=8, n1=4, o1=m1%n1;
		System.out.println(o1);
		
		//or
		
		int m2=8, n2=4;
		int o2;
	
		System.out.println(o2=m2%n2);  
		
		
		
		//Add two numbers and print the result:
		
		int num1=10;
		int num2=5;
		int sum=num1+num2;
		System.out.println("the sum is:"+" "+sum);
		
		//Subtract two numbers and print the result:
    
		int num3=10;
		int num4=5;
		int sub=num3-num4;
		System.out.println("the difference is:"+" "+sub);
		
		//Multiply two numbers and print the result:
		
		int num5=10;
		int num6=5;
		int mul=num5*num6;
		System.out.println("the product is:"+" "+mul);
		
		//Divide two numbers and print the result:
		
		int num7=10;
		int num8=5;
		double div=num7/num8;
		System.out.println("the divider is:"+" "+div);
		
		//Calculate the remainder of dividing two numbers and print the result:
		
		int num9=10;
		int num10=5;
		double re=num9%num10;
		System.out.println("the remainder is:"+" "+re);
		
		//Calculate the average of three numbers and print the result:
		
		int num11=10;
		int num12=20;
		int num13=30;
		
		int ave=num11+num12+num13/2;
		System.out.println("the average is:"+" "+ave);
		
		
		
		System.out.println("Mark Sheet ");
		System.out.println("---------------");
		int tamil=92;
		int en=63;
		int ma=45;
		int si=94;
		int so=98;
		System.out.println("TAMIL:"+tamil+"/100");
		
		System.out.println("ENGLISH:"+en+"/100");
		System.out.println("MATHS:"+ma+"/100");
	    System.out.println("SCEINCE:"+si+"/100");
	    System.out.println("SOCIAL:"+so+"/100");
	   
	   
	   
	}

}
