package all;

public class relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Relational
		
		/* relational operators used to comparing two values or variables 
		 * the values are known as boolean true or false
		 * 
		 *  == equal to
		 *  != not equal
		 *  > greater than
		 *  < less than
		 *  >= greater than or equal to
		 *  <= less than or equal to
		 *  
		 * */
		
		int a=17;
		int b=30;
		System.out.println(a<b);
		System.out.println(a>b);
		
		int c=288487863;
		int d=234567890;
		System.out.println(c<d);
		System.out.println(c>d);
		
		int e=3;
		int f=4;
		System.out.println(e==f);
		System.out.println(e!=f);
		System.out.println(e>=f);
		System.out.println(e<=f);
		
		
		int we=4;
		int ds=9;
		System.out.println(we==ds);
		
		
		
		
		
		
		
		
		
	}

}
